<!DOCTYPE html><html>    <head>        <meta charset="utf-8" />        <title>IOT Remote</title>    </head>    <body style="background-color: white;">       <h1 style="align:center;">  LED Control	</h1>     <!-- On/Off button's picture -->	 <?php	 //this php script generate the first page in function of the gpio's status	 $status = array (0, 0, 0, 0);	 for ($i = 0; $i < count($status); $i++) {		//set the pin's mode to output and read them		system("gpio mode ".$i." out");		exec ("gpio read ".$i, $status[$i], $return );			//if off		if ($status[$i][0] == 0 ) {		echo ("<img id='button_".$i."' src='pic/off".$i.".png'  alt='off' />");		}		//if on		if ($status[$i][0] == 1 ) {		echo ("<img id='button_".$i."' src='pic/on".$i.".png' alt='on'/>");		}	 	 }	 ?>	 <!-- javascript -->	 <script src="script.js"></script>    </body></html>